import { Logger } from '../utils';
/**
 * Message detail object
 */
export interface MessageDetail {
    code: string;
    message: string;
    severity: string;
}
/**
 * Structure of a Gateway response message
 */
export interface SuccessMessage {
    code: string;
    message: string;
    details: MessageDetail[];
}
/**
 * Structure of a Gateway error
 */
export interface ErrorMessage {
    code: string;
    message: {
        lang?: string;
        value: string;
    };
    innererror: {
        transactionid: string;
        timestamp: string;
        Error_Resolution: object;
        errordetails: MessageDetail[];
    };
}
/**
 * Log a Gateway response
 * @param msg message returned from gateway
 * @param log logger to be used
 */
export declare function prettyPrintMessage(msg: SuccessMessage, log: Logger): void;
/**
 * Log a Gateway error
 * @param msg error message returned from gateway
 * @param log logger to be used
 */
export declare function prettyPrintError(error: ErrorMessage, log: Logger): void;
