import { listSAPSystems } from '../../../src/system/index';
import * as connection from '../../../src/connection/index';

import { mocked } from 'ts-jest/utils';

jest.mock('../../../src/connection/index');
const mockConnection = mocked(connection, true);

describe('SAP Systems', () => {
    test('listSAPSystems returns something when not in AppStudio', async () => {
        mockConnection.isAppStudio.mockReturnValueOnce(false);
        await expect(async () => {
            await listSAPSystems();
        }).not.toThrowError();
    });
});
